import { setLocalStream, callStates, setCallState, setCallingDialog, setCallerUsername  } from "../../store/actions/call";
import store from "../../store/store";
import { sendPreOffer, sendPreOfferAnswer } from "../wssConnection/wssConnection";

const defaultConstrains = {
    video: true,
    audio: true
};

const preOfferAnswers = {
    CALL_ACCEPTED: 'CALL_ACCEPTED',
    CALL_REJECTED: 'CALL_REJECTED',
    CALL_NOT_AVAILABLE: 'CALL_NOT_AVAILABLE'
};

export const getLocalStream = () => {
    navigator.mediaDevices.getUserMedia(defaultConstrains)
    .then(stream => {
        console.log({stream});
        store.dispatch(setLocalStream(stream));
        store.dispatch(setCallState(callStates.CALL_AVAILABLE));
    })
    .catch(e => {
        console.log('Error occured when user trie dto get local stream');
        console.log(e);
    })
};

let connectedUserSocketId;

export const callToOther = (activeUserDetails) => {
    connectedUserSocketId =  activeUserDetails.socketId;
    store.dispatch(setCallState(callStates.CALL_IN_PROGRESS));
    store.dispatch(setCallingDialog(true));
    sendPreOffer({
        callee: activeUserDetails,
        caller: {
            username: store.getState().dashReducer.username
        }
    })

};

export const handlePreoffer = (data) => {
    if (checkIfCallIsPossible()) {
        connectedUserSocketId = data.callerSocketId;
        store.dispatch(setCallerUsername(data.callerUsername));
        store.dispatch(setCallState(callStates.CALL_REQUESTED));
    } else {
        sendPreOfferAnswer({
            callerSocketId: data.callerSocketId,
            answer: preOfferAnswers.CALL_NOT_AVAILABLE
        });
    }
};

export const acceptIncomingCallRequest = () => {
    sendPreOfferAnswer({
        callerSocketId: connectedUserSocketId,
        answer: preOfferAnswers.CALL_ACCEPTED
    });
};

export const rejectIncomingCallRequest = () => {
    sendPreOfferAnswer({
        callerSocketId: connectedUserSocketId,
        answer: preOfferAnswers.CALL_REJECTED
    });
};

// If user rejects call
export const resetCallData = () => {
    connectedUserSocketId = null;

}

// Check if call is possible
export const checkIfCallIsPossible = () => {
    if(store.getState().call.localStream === null || store.getState().callState !== callStates.CALL_AVAILABLE) {
        return false;
    }
    return true;
};
