import { SET_LOCAL_STREAM } from '../actions/callAction';

const initialState = {
    localStream: null,
    remoteStream: null
};

const callActionReducer = (state = initialState, action) => {

    switch(action.type) {
        case SET_LOCAL_STREAM:
            return {
                ...state,
                localStream: action.localStream
            }

        default: return state;
    }
};

export default callActionReducer;
