import {
  SET_LOCAL_STREAM,
  callStates,
  CALL_SET_STATE,
  SET_CALLING_DIALOG,
  SET_CALLER_USERNAME,
  SET_CALL_REJECTED,
} from "../actions/call";

const initialState = {
  localStream: null,
  remoteStream: null,
  callState: callStates.CALL_UNAVAILABLE,
  callingDialogVisible: false,
  callerUsername: "",
  rejectReason: {
      rejection: false,
      reason: ''
  }
};

const callReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_LOCAL_STREAM:
      return {
        ...state,
        localStream: action.localStream,
      };
    case CALL_SET_STATE:
      return {
        ...state,
        callState: action.callState,
      };
    case SET_CALLING_DIALOG:
      return {
        ...state,
        callingDialogVisible: action.dialog,
      };
    case SET_CALLER_USERNAME:
      return {
        ...state,
        callerUsername: action.username,
      };

    case SET_CALL_REJECTED:
        return {
            ...state,
            rejectReason: action.rejectDetails
        }

    default:
      return state;
  }
};

export default callReducer;
