import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
    MdCallEnd,
    MdMic,
    MdMicOff,
    MdVideocam,
    MdVideocamOff,
    MdScreenShare,
    // MdVideoCall,
    // MdCamera
} from 'react-icons/md';

import ConversionButton from './ConversionButton';
import { setLocalCameraEnabled, setLocalMicrophoneEnabled } from '../../../store/actions/call';

const styles = {
   btnContainer: {
    display: 'flex',
    position: 'absolute',
    bottom: '22%',
    left: '35%'
   },
   icon: {
    width: '25px',
    height: '25px',
    fill: '#e6e5e8'
   }
};


const ConversationButtons = ({localMircophoneEnabled, localCameraEnabled}) => {
    const dispatch = useDispatch();

    const localStream = useSelector(state => state.call.localStream);

    const handleMicButtonHandler = () => {
        const micEnabled = localMircophoneEnabled;
        localStream.getAudioTracks()[0].enabled = !micEnabled;
        console.log({micEnabled});
        dispatch(setLocalMicrophoneEnabled(!micEnabled));
    };

    const handlerCameraButtonHandler = () => {
        const cameraEnabled = localCameraEnabled;
        localStream.getVideoTracks()[0].enabled = !cameraEnabled;
        console.log({cameraEnabled});
        dispatch(setLocalCameraEnabled(!cameraEnabled));
    };

    return (
        <div style={styles.btnContainer}>
            <ConversionButton onClickHandler={handleMicButtonHandler}>
                {localMircophoneEnabled ? <MdMic style={styles.icon} /> : <MdMicOff style={styles.icon} />}
            </ConversionButton>
            <ConversionButton>
                <MdCallEnd style={styles.icon} />
            </ConversionButton>
            <ConversionButton onClickHandler={handlerCameraButtonHandler}>
                {localCameraEnabled ? <MdVideocam style={styles.icon} /> : <MdVideocamOff style={styles.icon} />}
            </ConversionButton>
            <ConversionButton>
                <MdScreenShare style={styles.icon} />
            </ConversionButton>
        </div>
    );
};

export default ConversationButtons;
